function reveal(){for(var reveals=document.querySelectorAll(".reveal"),i=0;i<reveals.length;i++){var windowHeight=window.innerHeight;reveals[i].getBoundingClientRect().top<windowHeight-150?reveals[i].classList.add("active"):reveals[i].classList.remove("active")}}jQuery(document).ready(function($){$(".btn-nav.open").click(function(e){e.preventDefault(),$(".nav-global").animate({left:"0"})}),$(".btn-nav.close").click(function(e){e.preventDefault(),$(".nav-global").animate({left:"-100vw"})})}),window.addEventListener("scroll",reveal);
